mapper.select:
SELECT count(*) from approval
where DRAFTER = #{ empId }
and APPR_DATE between #{ begin } and (to_date(#{ end }) + 1 - 1/86400)

select * 
from draft_annual
where APPR_ID = #{ apprId }
and DRAFT_TYPE = #{ draftType }

select count(*) from approval 
where drafter = #{ empId }
and APPR_STATE = 'complete'

select count(*) from approval 
where drafter = #{ empId }
and APPR_STATE = 'continue'

select count(*) from approval 
where drafter = #{ empId }
and APPR_STATE = 'companion'

select count(*)
from (select APPR_ID, DRAFTER, DRAFT_TYPE, LINE_ID,
      APPR_STATE, APPR_DATE, RECEIPT_DATE, REJECTION_DATE,
      EA.emp_name AS approver_name
      from approval A
      join employee EA on (A.approver = EA.emp_id)
      where DRAFTER = #{ empId }
    )
where approver_name like '%' || #{ keyword } || '%'

select count(*)
from (select APPR_ID, DRAFTER, DRAFT_TYPE, LINE_ID,
