mapper.select:
select count(*) 
from UPLOAD_BOARD
where DEP_ID = #{ depId } and BOARD_ID = #{ boardId }

mapper.update:
update board set
VIEWS_NUM = VIEWS_NUM + 1
where DEP_ID = #{ depId } and BOARD_ID = #{ boardId }

update upload_board set 
UPLOAD_URL = #{ uploadUrl },
RENAME_URL = #{ renameUrl }
where DEP_ID = #{ depId } 
and BOARD_ID = #{ boardId }

update board set
BOARD_TITLE = #{ boardTitle },
BOARD_DETAIL = #{ boardDetail },
MODIFY_DATE = sysdate
where DEP_ID = #{ depId }
and BOARD_ID = #{ boardId }
