mapper.select:
SELECT BOOK_NAME
FROM  BOOK
WHERE BOOK_ID= #{ bookId }

SELECT CLIENT_NAME
FROM CLIENT
WHERE CLIENT_ID = #{ storageId }

SELECT *
FROM INVENTORY
WHERE INVEN_DATE BETWEEN #{ startRow } AND #{ endRow }


SELECT *
FROM (SELECT ROWNUM RNUM, INVEN_ID, BOOK_ID, STORAGE_ID, STORE_ID, REFUND_ID, PREV_INVEN_ID, INCREASE, CURR_INVEN, INVEN_DATE, CLASSIFY, REMARK 
      FROM (SELECT *
            FROM INVENTORY
            WHERE INVEN_DATE between to_date(#{ begin }) and to_date(#{ end }) + 1 - 1/86400
            ORDER BY INVEN_DATE DESC
            )
      )
WHERE RNUM between #{ startRow } and #{ endRow }
